import React from 'react';


class CountryApi extends React.Component {






    constructor(props){
        super(props);
        this.state = {
            data: ""
        }
    }

    render(){
        if(this.state.data === ""){
            return null
        }else{
            return (
                <div>
                    <h1>{this.state.data}</h1>
                </div>
                )
        }

    }
    componentDidMount(){

    fetch('https://api.chucknorris.io/jokes/random').then( resp =>resp.json()).then( joke => {
        this.setState({
            data: joke.value
        })
      }).catch( err => {
        console.log('Błąd!', err);
      });

    }
}







export default CountryApi;
