import React from 'react';
import './gameBoard.css'
import ReactBoard from 'react-board';

function Square(props) {
    const {value} = props;
    return (
      <button className="square" onClick={props.onClick}>
        {value}
      </button>
    );
}



class BoardForGame extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          squares: Array(9).fill(null),
          xIsNext: true
        };
      }
      renderSquare(i) {
        const {squares} = this.state;
        return (
          <Square
            value={squares[i]}
            onClick={() => this.handleClick(i)}
          />
        );
      }

    } 


class Game extends React.Component {




  render(){
    let userRows = prompt("Provide number of rows");
    let userColumns = prompt("Provide number of columns");
      return(
          <BoardForGame userRows ={userRows} userColumns={userColumns}/>

      )
  }
    }


export default Game;
