import React from 'react';
import { useSpring, animated } from 'react-spring'
import './styles.css'
import Modal from 'react-responsive-modal';


class Windows extends React.Component {
  state = {
    open: false,
  };

  onOpenModal = () => {
    this.setState({ open: true });
  };

  onCloseModal = () => {
    this.setState({ open: false });
  };

  render() {
    const { open } = this.state;
    return (
      <div className ="modalContainer">
      <button>xddd</button>

        <img src={require("./imagies/question_mark_yellow_new.png")} onClick={this.onOpenModal} className="modal" alt="more info"/>
        <Modal open={open} onClose={this.onCloseModal} little>

         <p style ={{marginRight:"20px"}}>Good or bad? Choose your character and save or manipulate people from the city! After the game starts, you have 30 seconds to click on a person and collect points for that. Be careful - people don’t trust you, no matter which side you choose and it’s not easy to click on them! Good luck!</p>
        </Modal>
      </div>
    );
  }
}



const calc = (x, y) => [x - window.innerWidth / 2, y - window.innerHeight / 2]
const trans1 = (x, y) => `perspective(600px) rotateX(${-x / 100}deg) rotateY(${-y / 100}deg) translate3d(${x / 10}px,${y / -10}px,0)`
// const trans2 = (x, y) => `translate3d(${x /100}px,${y/10}px,0)`
const trans3 = (x, y) => `perspective(600px) rotateX(${x / 100}deg) rotateY(${y / 100}deg) translate3d(${x / 6 - 250}px,${y / 6 - 120}px,0)`
const trans4 = (x, y) => `translate3d(${x / 10}px,${y/10}px,0)`

function MainAnimation() {
  const [props, set] = useSpring({ xy: [0, 0] })
  return (
    <div className="container" onMouseMove={({ clientX: x, clientY: y }) => set({ xy: calc(x, y) })}>
      <animated.div className="secondBackground" style={{ transform: props.xy.interpolate(trans1) }} />
      <animated.div className="star1" style={{ transform: props.xy.interpolate(trans3) }} />
      <animated.div className="star2" style={{ transform: props.xy.interpolate(trans3) }} />
      <animated.div className="star3" style={{ transform: props.xy.interpolate(trans4) }} />
      <animated.div className="star4" style={{ transform: props.xy.interpolate(trans3) }} />
      <animated.div className="star5" style={{ transform: props.xy.interpolate(trans3) }} />
      <animated.div className="star6" style={{ transform: props.xy.interpolate(trans3) }} />
      <Windows/>

    </div>
  )
}


export default MainAnimation;
