import React from 'react';
import './gameBoard.css'



class Table extends React.Component{




    handleClick = ()=>{
        if(typeof this.props.onClick === "function"){
            this.props.onClick();
        }
    }




    render(){

        return (

            <table>
                <tbody>
                    <tr>
            <td className ="float"><img src={require(`${this.props.img}`)} className ="cell" onClick = {()=>this.handleClick} alt ="x"/> </td>
            </tr>
                </tbody>
            </table>       


        )
    }
}


class Game extends React.Component{
    constructor(props){
        super(props)
        this.state={
            counter: 0,
            image: "./imagies/joker-white.jpg"



        }
    }

    changeForBatman = () =>{
        if ( this.state.image ==="./imagies/joker-white.jpg"){
            this.setState({
                counter: this.state.counter +1,
                image: "./imagies/batman-black.jpg"

            })

        }else {
            console.log("Karta juz zostala odwrocona ")
        }
    }
    render(){
        return (
            <div>
            <p>{this.state.counter}</p>


            <Table onClick={this.changeForBatman} img = {this.state.image}></Table>




              <Table onClick={this.changeForBatman} img ={this.state.image}></Table>




             </div>
        )
    }
}

export default Game;
