import React from 'react';
import './gameBoard.css'


class Game extends React.Component {

    randomBatman = (urows,ucolumns) =>{
        let calcBoard = urows * ucolumns
        let randomNr = Math.round(Math.random() * (calcBoard - 1) + 1);
        console.log(randomNr)
        for (let i =0; i<randomNr; i++){
            let finalIndex = Math.round(Math.random() * (calcBoard - 1) + 1);
            console.log(finalIndex)
        }






    }

    boardGame = (urows, ucolumns) => {
        // Build the rows in an array
        let rows = [];
        for (let y = 0; y < urows; y++) {
            const cells = [];
            for (let x = 0; x < ucolumns; x++) {
                cells.push(<Cell  key={x} />);
            }
            // Put them in the row
            rows.push(<tr key={y}>{cells}</tr>);
        }

        // Return the table
        return (
          <table>
            <tbody>
              {rows}
            </tbody>
          </table>
        );
    }

    render() {
        let userRows = prompt("Provide number of rows");
        let userColumns = prompt("Provide number of columns");

        return (
          <div>
            {this.boardGame(userRows,userColumns)}
            <button onClick ={this.randomBatman(userRows,userColumns)}>Start generate random Batman</button>
          </div>
        );
    }
}

class Cell extends React.Component {



    render() {
        return (
          <td>
            <img
              src={require("./imagies/joker-white.jpg")}

              className="cell"
              alt="x" />
          </td>
        );
    }
}



export default Game;
