import React from 'react';
import './gameBoard.css'



class Table extends React.Component{
    constructor(props){
        super(props)
        this.state={
            counter: 0,
            image: ""



        }
    }




    handleClick = ()=>{
        if(typeof this.props.onClick === "function"){
            this.props.onClick();
        }
    }




    render(){

        return (

            <td className ="float"><img src={require(`${this.props.image}`)} className ="cell" onClick = {this.handleClick} alt ="x"/> </td>



        )
    }
}


class Game extends React.Component{
    constructor(props){
        super(props)
        this.state={
            counter: 0,
            image: "./imagies/joker-white.jpg"



        }
    }

    changeForBatman = () =>{
        if ( this.state.img ==="./imagies/joker-white.jpg"){
            console.log("dziala")
            this.setState({
                counter: this.state.counter +1,
                image: "./imagies/batman-black.jpg"

            })

        }else {
            console.log("Karta juz zostala odwrocona ")
        }
    }
    render(){
        return (
            <div>
            <p>{this.state.counter}</p>


            <table>
                <tbody>
                    <tr>
            <Table onClick={this.changeForBatman} image ={this.state.image}></Table>
                 </tr>
                </tbody>
            </table>  


            <table>
                <tbody>
                    <tr>
              <Table onClick={this.changeForBatman} ></Table>
              </tr>
                </tbody>
            </table>  



             </div>
        )
    }
}

export default Game;
