import React from 'react';
import FlagsApi from './api'
import Gender from './gender'
import {Link} from 'react-router-dom'



class PlayerForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            nick: "",
            email: "",
            good_bad:"",
            country:"",
            finalResult: "",
            color:""
        }
    }
    playerNick = (event) => this.setState({nick: event.target.value})
    playerEmail = (event) => this.setState({email: event.target.value})
    playerGender = (event) => this.setState({good_bad: event.target.value})
    playerCountry = (event) => this.setState({country: event.target.value})

    handleSubmit = (event) => {
        event.preventDefault(); 
        let result = "";
        if (this.state.nick === "") {
            result = "Entry you nick name"
          this.setState({color:"red"});
        } else if (this.state.email.indexOf("@")<0){
            result = "Entry your email"
          this.setState({color:"red"});
        } else if (this.state.good_bad === "") {
            result = "Entry you side do you assist "
          this.setState({color:"red"});
        }else if ( this.state.country ===""){
            result = "Entry your country"
        } else {

          this.setState({color:"green"});
          result= "Let's start" + this.state.nick
        }

        this.setState({finalResult: result})
    }

    intention = (selectedByUser) =>{
        console.log(this.state.good_bad)
 if (selectedByUser === "good"){
      let intentionResult = ["./imagies/batman.png","./imagies/cat.png"]
      intentionResult.map((newSrc,index)=>{
        return <img key={index} style={{width:"100%", height:"100%"}} src={require(`${newSrc}`)}
        alt="ccccd"/> })

         console.log("ccc")
         } else {
        let intentionResult = ["./imagies/joker-female.png","./imagies/joker.png"]
        intentionResult.map((newSrc,index)=>{
            return <img key={index} src={require(`${newSrc}`)}
                alt="ccccd"/> })

         }
    }
    render() {


        return (
            <div>
                {/* onChange={this.playerGender} */}
                <form onSubmit={this.handleSubmit}>
                    <h1 style={{backgroundColor:this.state.color}}>{this.state.finalResult}</h1>
                    <input type="text" onChange={this.playerNick} value={this.state.nick} placeholder="Your nick"/><br></br>
                    <input type="text" onChange={this.playerEmail} value={this.state.email} placeholder="Your email" /><br></br>
                    <select value={this.state.good_bad}  onChange={this.intention(this.state.good_bad)}> 
                        <option value ="/">--</option>
                        <option value="good">Good</option>
                        <option value="bad">Bad</option>
                </select> <br></br>
                <input type="text" onChange={this.playerCountry} value={this.state.country} placeholder="Your name country"/><br></br>
                    <input type="submit" value="Wyślij"/>
                </form>

                 <FlagsApi/> 
                 <Gender />
                 <button><Link to= "/player/game">Play </Link></button>
            </div>
        )
    }
}

export default PlayerForm;
