        var lastSpace = str.lastIndexOf(' ');

        return str.substr(0, lastSpace);
   }
    render(){
        console.log(this.state.time)
        var string = "2019-04-28 10:23:26.643+0200";
        let chngStr = string.substring();
        console.log(chngStr)
        var lastSpace = chngStr.lastIndexOf(' ');
        console.log(chngStr.substr(9, lastSpace))

      return(<div onChange={this.getSubstring}> {this.state.time}</div>)
    }
}

