import React from 'react';

class FlagsApi extends React.Component{
    constructor(props){
        super(props);
        this.state = {}
    }

    componentDidMount() {
        console.log(this.state);

        fetch('https://api.ipgeolocation.io/ipgeo?apiKey=080053faad5448749930b6bdb1ec0a7f')

        .then(response => {

            response.json().then(data => {
                this.setState(data);
                console.log(response)
                console.log(data)
            });
            response.json().then(data1=>{
                timeFunc(data1)  });


        }).catch(err => {
            console.log('Błąd!', err);
        });
    }
    timeFunc = () =>{
        let arr = [];

        <div>{this.state.time_zone.map((time,index)=>{
            console.log(time)
          return <p key ={index}>{time.current_time}</p>

      })}</div>

    }

    render() {
        console.log(this.state.time_zone)
        return(
            <div>
                <h1>{this.state.country_name}</h1>

                <img src={this.state.country_flag} alt="" />


            </div>
        )
    }
}

export default FlagsApi;
