import React from 'react';



class HeroImg extends React.Component{
    render(){
        let intentionResult;
        if (this.state.good_bad === "good"){
            intentionResult = ["./imagies/batman.png","./imagies/cat.png"]

               console.log("ccc")
               } else if (this.state.good_bad  === "bad") {
              intentionResult = ["./imagies/joker-female.png","./imagies/joker.png"]

               }else return null
        return(<div>
            <div>{intentionResult.map((newSrc,index)=>{
                return <img style={{width:"10%", height:"30%"}} key={index} src={require(`${newSrc}`)}
            alt="ccccd" onClick ={this.selected}/> })} </div>
            </div>
        )
    }
}
export default HeroImg;


