import React from 'react';
import FinalGame from './finalGame'
import './gameBoard.css'
import './classHeros.css'





class HeroImg extends React.Component{
    constructor(props){
        super(props);
        this.state={
            heros:""
        }
    }


    render(){
             let imgPeople= <img src={require("./imagies/person.png")} className ="cell" alt="person"/>
        let arrForPeople =[];
            for ( let i=0; i<10; i++){
                arrForPeople.push(imgPeople)
            }
        console.log(this.props.selectedIntention)
        let intentionResult;
        if (this.props.selectedIntention === "good"){
            // intentionResult = ["./imagies/batman.png","./imagies/cat.png"]
             intentionResult =<div className="goodPlace" id="goodBoard"><div><img src={require("./imagies/batman.png")} className="goodHeros"alt=""/><img src={require("./imagies/cat.png")} className="goodHeros" alt=""/></div></div>

            this.setState({heros:"good"})

               } else if (this.props.selectedIntention === "bad") {
            //   intentionResult = ["./imagies/joker-female.png","./imagies/joker.png"]
              this.setState({heros:"bad"})

            intentionResult =<div className="badPlace"><img src={require("./imagies/joker-female.png")} className="badHeros" alt=""/><img src={require("./imagies/joker.png")} className="badHeros" alt=""/></div>

        }else {return null}

        return(<div>

                {intentionResult}
                <FinalGame position={'absolute'} imgages={arrForPeople} class={this.state.heros}/>

            {/* {intentionResult.map((newSrc,index)=>{
                return <img className={this.state.class} key={index} src={require(`${newSrc}`)}
            alt="ccccd" onClick ={this.selected}/> })}  */}

                </div>

        )
        }}


export default HeroImg;
