import React from 'react';

class FlagsApi extends React.Component{
    constructor(props){
        super(props);
        this.state = {}
    }

    componentDidMount() {
        console.log(this.state);

        fetch('https://api.ipgeolocation.io/ipgeo?apiKey=080053faad5448749930b6bdb1ec0a7f')

        .then(response => {

            response.json().then(data => {
                this.setState(data);
                console.log(response)
                console.log(data)
            });
            response.json().then(data1=>{
                timeFunc(data1)  });


        }).catch(err => {
            console.log('Błąd!', err);
        });
    }




    render() {
        timeFunc = () =>{
            let curTime = this.state.time_zone.map((time,index)=>{
            console.log(time)
          return<div>{time.current_time}</div> 

      })}

        return(
            <div>
                <h1>{this.state.country_name}</h1>
                <div>{curTime}</div>
                <img src={this.state.country_flag} alt="" />


            </div>
        )
    }
}

export default FlagsApi;
