import React from 'react';
import { AwesomeButton } from "react-awesome-button";
import "react-awesome-button/dist/styles.css";
import {Link} from 'react-router-dom'
import Modal from 'react-bootstrap/Modal'
import ModalDialog from 'react-bootstrap/ModalDialog'

class Example extends React.Component {
    constructor(props, context) {
      super(props, context);

      this.state = {
        smShow: false,
        lgShow: false,
      };
    }

    render() {

      let lgClose = () => this.setState({ lgShow: false });

      return (
        <ButtonToolbar>

          <Button onClick={() => this.setState({ lgShow: true })}>
            Large modal
          </Button>


          <Modal
            size="lg"
            show={this.state.lgShow}
            onHide={lgClose}
            aria-labelledby="example-modal-sizes-title-lg"
          >
            <Modal.Header closeButton>
              <Modal.Title id="example-modal-sizes-title-lg">
                Large Modal
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>...</Modal.Body>
          </Modal>
        </ButtonToolbar>
      );
    }
  }


class BottomAnimation extends React.Component {
    constructor(props){
        super(props);
        this.state={
            view: this.props.car
        }

    }
    // handleClick = ()=>{
    //     console.log("dziala")
    // }

    componentDidMount =()=>{
        let suprise= 
        <div>
            <AwesomeButton type="secondary" style={{
               border:"10px solid rgb(125, 99, 177)",
               opacity:".6",
               height:"20%",
               fontSize: "5rem",
               position: "absolute",
               transform:"translateY(-50%) translateX(50%)",
               top: "70%",
               right: "50%",
               width:"20%"  
            }}>
                <Link to= "/player" style={{textDecorationLine:"none"}}>Play </Link>
            </AwesomeButton>
        </div>
        this.timeout = setTimeout(()=>{
            this.setState({
             view: suprise
            })
        },4000);

    }
    componentWillUnmount(){
        clearTimeout(this.timeout);
    }

    render(){
        return(
            <div><Example/>
            <div> {this.state.view}</div>
            </div>
        )
    }
}



export default BottomAnimation;
