import React from 'react';
import FinalGame from './finalGame'
import './gameBoard.css'
import './classHeros.css'





class HeroImg extends React.Component{


    render(){
             let imgPeople= <img src={require("./imagies/person.png")} className ="cell" alt="person"/>
        let arrForPeople =[];
            for ( let i=0; i<10; i++){
                arrForPeople.push(imgPeople)
            }
        console.log(this.props.selectedIntention)
        let intentionResult;
        if (this.props.selectedIntention === "good"){
            // intentionResult = ["./imagies/batman.png","./imagies/cat.png"]
             intentionResult =<div className="goodPlace"><img src={require("./imagies/batman.png")} className="goodHeros"alt=""/><img src={require("./imagies/cat.png")} className="goodHeros" alt=""/> <FinalGame position={'absolute'} imgages={arrForPeople}/></div>

            // this.setState({'active': false,'class': 'album'})

               } else if (this.props.selectedIntention === "bad") {
            //   intentionResult = ["./imagies/joker-female.png","./imagies/joker.png"]
            //   this.setState({'active': true,'class': 'active'})

            intentionResult =<div className="badplace"> <img src={require("./imagies/joker-female.png")} className="badHeros" alt=""/><img src={require("./imagies/joker.png")} className="badHeros" alt=""/> <FinalGame position={'absolute'} imgages={arrForPeople}/><FinalGame position={'absolute'} imgages={arrForPeople}/></div>

        }else {return null}

        return(<div>

                {intentionResult}

            {/* {intentionResult.map((newSrc,index)=>{
                return <img className={this.state.class} key={index} src={require(`${newSrc}`)}
            alt="ccccd" onClick ={this.selected}/> })}  */}

                </div>

        )
        }}


export default HeroImg;
