import React from 'react';
import './gameBoard.css'



class BoardGame extends React.Component {
    constructor (props){
        super(props);
        this.state={
            counter : 0,
            result: null 
        }
    }
    handleClick = (event)=>{
        console.log(event.target)
        console.log(event.getAttribute('id'))

        this.setState({
        counter: this.state.counter +1})
        console.log(this.state.counter)
        if (this.state.counter === 0 || this.state.counter%2 === 0  ){
            console.log("gr1")
            this.setState({
                result: "X"

            })
        }else {console.log("gr2")
            this.setState({
                result: "O"


        })
    }


    }


 render(){




     return (
        <table>
            <tbody>
            <tr>
                <td id = "1"  onClick={(e)=>{this.handleClick(e.target)}}   className="cell" alt ="board"></td>
                <td id = "2"  onClick={(e)=>{this.handleClick(e.target)}}   className="cell" alt ="board"></td>
                <td id = "3"  onClick={(e)=>{this.handleClick(e.target)}}   className="cell" alt ="board"></td>
            </tr>   
            <tr>
                <td id = "4"  onClick={(e)=>{this.handleClick(e.target)}}   className="cell" alt ="board"></td>
                <td id = "5"  onClick={(e)=>{this.handleClick(e.target)}}   className="cell" alt ="board"></td>
                <td id = "6"  onClick={(e)=>{this.handleClick(e.target)}}   className="cell" alt ="board"></td>
            </tr>  
            <tr>
                <td id = "7"  onClick={(e)=>{this.handleClick(e)}}   className="cell" alt ="board"></td>
                <td id = "8"  onClick={(e)=>{this.handleClick(e)}}   className="cell" alt ="board"></td>
                <td id = "9"  onClick={(e)=>{this.handleClick(e)}}   className="cell" alt ="board"></td>
            </tr>  

            </tbody>

    </table>



     )
 }
}


export default BoardGame;
