import React from 'react';
import './gameBoard.css'



class Table extends React.Component{
    constructor(props){
        super(props)
            this.state={
                counter: 0,
                image: "./imagies/joker-white.jpg"


        }
    }
    changeForBatman = () =>{
        if ( this.state.image ==="./imagies/joker-white.jpg"){
            this.setState({
                image: "./imagies/batman-black.jpg",
                counter: this.state.counter +1
            })

        }else {
            console.log("Karta juz zostala odwrocona ")
        }
    }



    render(){
        return (


                    <td><img src={require(`${this.state.image}`)} className ="cell" onClick = {() => this.changeForBatman()}/> </td>

        )
    }
}


class Game extends React.Component{
    render(){
        return (
            <div>
           <table>
               <tr><Table></Table></tr>

           </table>
                 <table>
                 <tr><Table></Table></tr>

             </table>
             <table>
                 <tr><Table></Table></tr>

             </table>
             </div>

        )
    }
}

export default Game;
