}



class Cell extends React.Component {
    constructor(props){
        super(props)
            this.state={
                image:"./imagies/joker-white.jpg",
                counter: 0


        }
    }
    changeForBatman = () =>{
        if ( this.state.image ==="./imagies/joker-white.jpg"){
            this.setState({
                image: "./imagies/batman-black.jpg",
                counter: this.state.counter +1
            })

        }else {
            console.log("Karta juz zostala odwrocona ")
        }
    }


    render() {
        let final =  <img
        src={require(`${this.state.image}`)}
        onClick = {() => this.changeForBatman()}
        className="cell"
        alt="x" />
        console.log(this.state.counter)

        return (
            <div>
            <td>{final}</td>
            <div>{this.state.counter}</div>

            </div>


        );
    }
}




export default Game;
