    // }
    changeForBatmans = () =>{
        //need to change img during click 
        console.log(this.state.image)
        if ( this.state.image ==="./imagies/joker-white.jpg"){
            this.setState({
                image: "./imagies/batman-black.jpg"
                // counter: this.state.counter +1



            })
            // console.log(this.state.counter)
        }else {
            // this.setState({
            //     image: "./imagies/joker-white.jpg"

            // })
            console.log( "to pole juz bylo klikniete")
        }
    }
    // }
    // componentDidMount(){
    //     this.timeoutId = setTimeout(()=>{
    //     this.setState({
    //         alert:"start click"
    //     })
    //         },5000)

    // }
    // componentWillUnmount(){
    //     clearTimeout(this.timeout);
    //     this.setState({
    //         alert: `your score is ${this.state.counter} `
    //     })
    // }



    render() {




        let final =  <img
        src={require(`${this.state.image}`)}

        onClick = {() => this.changeForBatmans}
        className="cell"
        alt="x" />

        return (

            <td>{final}</td>


        );
    }
}



export default Game;
