import React from 'react';
import './gameBoard.css'



class Table extends React.Component{
    constructor(props){
        super(props)
        this.state={

            image: "./imagies/joker-white.jpg"



        }
    }



    handleClick = ()=>{
        if(typeof this.props.onClick === "function"){
            this.props.onClick();
        }
    }




    render(){

        return (
            <div>x</div>




        )
    }
}


class Game extends React.Component{
    constructor(props){
        super(props)
        this.state={
            counter: 0,
            image: "./imagies/joker-white.jpg"



        }
    }

    changeForBatman = () =>{
        if ( this.state.image ==="./imagies/joker-white.jpg"){
            this.setState({
                counter: this.state.counter +1,
                image: "./imagies/batman-black.jpg"

            })

        }else {
            console.log("Karta juz zostala odwrocona ")
        }
    }
    render(){
        return (
            <div>
            <p>{this.state.counter}</p>
            <table>
                <tbody>
                    <tr>
            <td className ="float"><img src={require(`${this.state.image}`)} className ="cell" onClick={this.changeForBatman} alt ="x"/> </td>
            </tr>
            <tr>
            <td className ="float"><img src={require(`${this.state.image}`)} className ="cell" onClick={this.changeForBatman} alt ="x"/> </td>
            </tr>
            <tr>
            <td className ="float"><img src={require(`${this.state.image}`)} className ="cell" onClick={this.changeForBatman} alt ="x"/> </td>
            </tr>
                </tbody>
            </table>      


            <Table onClick={this.changeForBatman}></Table>




              <Table onClick={this.changeForBatman} ></Table>




             </div>
        )
    }
}

export default Game;
