import React from 'react';
import FinalGame from './finalGame'
import './gameBoard.css'
import './classHeros.css'





class HeroImg extends React.Component{
    constructor(props){
        super(props);
        this.state={
            'active': false, 'class': 'album'

        }
    }

    render(){
             let imgPeople= <img src={require("./imagies/person.png")} className ="cell" alt="person"/>
        let arrForPeople =[];
            for ( let i=0; i<10; i++){
                arrForPeople.push(imgPeople)
            }
        console.log(this.props.selectedIntention)
        let intentionResult;
        if (this.props.selectedIntention === "good"){
            intentionResult = ["./imagies/batman.png","./imagies/cat.png"]
            //  intentionResult = [<img src={require("./imagies/batman.png")} className="goodHeros"alt=""/>,<img src={require("./imagies/cat.png")} className="goodHeros" alt=""/>]

            this.setState({'active': false,'class': 'album'})

               } else if (this.props.selectedIntention === "bad") {
              intentionResult = ["./imagies/joker-female.png","./imagies/joker.png"]
              this.setState({'active': true,'class': 'active'})

            // intentionResult = [<img src={require("./imagies/joker-female.png")} className="badHeros" alt=""/>,<img src={require("./imagies/joker.png")} className="badHeros" alt=""/>]

        }else return null

        return(<div>

            {intentionResult.map((newSrc,index)=>{
                return <img className={this.state.classHeros} key={index} src={require(`${newSrc}`)}
            alt="ccccd" onClick ={this.selected}/> })} 
                 </div>

        )
    }
}
export default HeroImg;



