import React from 'react';
import './gameBoard.css'



class BoardGame extends React.Component {
    constructor (props){
        super(props);
        this.state={
            counter : 0,
            result: null 
        }
    }
    clicked = (event)=>{
        console.log(event.target)

        this.setState({
        counter: this.state.counter +1})
        console.log(this.state.counter)
        if (this.state.counter === 0 || this.state.counter%2 === 0  ){
            console.log("gr1")
            this.setState({
                result: "X"

            })
        }else {console.log("gr2")
            this.setState({
                result: "O"


        })
    }


    }


 render(){




     return (
        <div id="board" onClick={(e)=>this.clicked(e.target)}>
              <div className="cell" data-square="0">{this.state.result}</div>
              <div className="cell" data-square="1">{this.state.result}</div>
              <div className="cell" data-square="2"></div>
              <div className="cell" data-square="3"></div>
              <div className="cell" data-square="4"></div>
              <div className="cell" data-square="5"></div>
              <div className="cell" data-square="6"></div>
              <div className="cell" data-square="7"></div>
              <div className="cell" data-square="8"></div>
          </div>



     )
 }
}


export default BoardGame;
