import React from 'react';
import { useSpring, animated } from 'react-spring'
import './styles.css'


// import {BrowserRouter as Router, Route, Link} from 'react-router-dom'


const calc = (x, y) => [x - window.innerWidth / 2, y - window.innerHeight / 2]
// const trans1 = (x, y) => `perspective(600px) rotateX(${-x / 100}deg) rotateY(${-y / 100}deg) translate3d(${x / 10}px,${y / -10}px,0)`
// const trans2 = (x, y) => `translate3d(${x /100}px,${y/10}px,0)`
const trans3 = (x, y) => `perspective(600px) rotateX(${x / 100}deg) rotateY(${y / 100}deg) translate3d(${x / 6 - 250}px,${y / 6 - 120}px,0)`
const trans4 = (x, y) => `translate3d(${x / 10}px,${y/10}px,0)`

function MainAnimation() {
  const [props, set] = useSpring({ xy: [0, 0] })
  return (
    <div className="container" onMouseMove={({ clientX: x, clientY: y }) => set({ xy: calc(x, y) })}>
      {/* <animated.div class="card1" style={{ transform: props.xy.interpolate(trans1) }} /> */}
      <animated.div class="card2" style={{ transform: props.xy.interpolate(trans3) }} />
      <animated.div class="card3" style={{ transform: props.xy.interpolate(trans3) }} />
      <animated.div class="card4" style={{ transform: props.xy.interpolate(trans4) }} />
      <animated.div class="card5" style={{ transform: props.xy.interpolate(trans3) }} />
      <animated.div class="card6" style={{ transform: props.xy.interpolate(trans3) }} />
      <animated.div class="card7" style={{ transform: props.xy.interpolate(trans3) }} />



    </div>
  )
}

