import React from 'react';
import './gameBoard.css'


class Game extends React.Component {
    constructor(props){
        super(props)
        this.state={
            row: [],
            cell:[]
        }
    }




    boardGame = (urows, ucolumns) => {
        // Build the rows in an array
        let rows = [];
        for (let y = 0; y < urows; y++) {
            const cells = [];
            for (let x = 0; x < ucolumns; x++) {
                cells.push(<Cell key={x}  />);
            }

            // Put them in the row
            rows.push(<tr key={y}>{cells}</tr>);

        }
       this.setState({
           cell : {cells},
           row: {rows}
       })




        // Return the table
        return (
          <table>
            <tbody>
              {rows}
            </tbody>
          </table>
        );
    }



    // function to remove!!!!!
    // randomBatmans = (urows1,ucolumns1) =>{
    //     // count all img 
    //     let calcBoard = urows1 * ucolumns1
