    //         let finalIndex = Math.round(Math.random() * (calcBoard - 1) + 1);
    //         console.log(finalIndex)
    //      arrForIndex.push(finalIndex);

    //     }
    //     console.log(arrForIndex)





    // }

    check = (ar)=>{

        //function to check neightbours 

            // if(this.state.cells[0].getAttribute('src') ==="./imagies/joker-white.jpg"){
        //     console.log("Dziala")
        // }else console.log("nie")

    }






    render() {
        let userRows = prompt("Provide number of rows");
        let userColumns = prompt("Provide number of columns");



        //     if(this.props.cells[0].getAttribute('src') ==="./imagies/joker-white.jpg"){
        //     console.log("Dziala")
        // }else console.log("nie")








        return (
            <div>
                <div>{this.boardGame(userRows,userColumns)}</div>

            {/* start generate random index during click on button  */}
                {/* <button onClick={() => this.randomBatmans(userRows,userColumns)}>play</button> */}
                <button onClick ={() => this.check()}>Check next population</button>




            </div>
        );
    }
}









class Cell extends React.Component {
    constructor(props){
        super(props)
            this.state={
                image:"./imagies/joker-white.jpg"


        }
    }
    changeForBatman = () =>{
        //need to change img during click 
        if ( this.state.image ==="./imagies/joker-white.jpg"){
            this.setState({
                image: "./imagies/batman-black.jpg"


            })
        }else {
            this.setState({
                image: "./imagies/joker-white.jpg"

            })
        }
    }


    render() {

        let final =  <img
        src={require(`${this.state.image}`)}

        onClick = {() => this.changeForBatman()}
        className="cell"
        alt="x" />

        return (
            <td>{final}</td>
        //     <td>
        //       <img
        //       src={require(`${this.state.image}`)}
        //       onClick = {() => this.changeForBatman()}
        //       className="cell"
        //       alt="x" />
        //   </td>
        );
    }
}


export default Game;
