import React from 'react';
import { AwesomeButton } from "react-awesome-button";
import "react-awesome-button/dist/styles.css";
import {Link} from 'react-router-dom'
import Modal from 'react-responsive-modal';

class Window extends React.Component {
  state = {
    open: false,
  };

  onOpenModal = () => {
    this.setState({ open: true });
  };

  onCloseModal = () => {
    this.setState({ open: false });
  };

  render() {
    const { open } = this.state;
    return (
      <div>
        <button onClick={this.onOpenModal}>Open modal</button>
        <Modal open={open} onClose={this.onCloseModal} little>

         <p style ={{marginRight:"20px"}}>Good or bad? Choose your character and save or manipulate people from the city! After the game starts, you have 30 seconds to click on a person and collect points for that. Be careful - people don’t trust you, no matter which side you choose and it’s not easy to click on them! Good luck!</p>
        </Modal>
      </div>
    );
  }
}



class BottomAnimation extends React.Component {
    constructor(props){
        super(props);
        this.state={
            view: this.props.car
        }

    }


    componentDidMount =()=>{
        let suprise= 
        <div>
            <AwesomeButton type="secondary" style={{
               border:"10px solid rgb(125, 99, 177)",
               opacity:".6",
               height:"20%",
               fontSize: "5rem",
               position: "absolute",
               transform:"translateY(-50%) translateX(50%)",
               top: "70%",
               right: "50%",
               width:"20%"  
            }}>
                <Link to= "/player" style={{textDecorationLine:"none"}}>Play </Link>
            </AwesomeButton>
        </div>
        this.timeout = setTimeout(()=>{
            this.setState({
             view: suprise
            })
        },4000);

    }
    componentWillUnmount(){
        clearTimeout(this.timeout);
    }

    render(){
        return(
            <div>
            <div> {this.state.view}</div>
            <Window className="modal"/>
            </div>
        )
    }
}



export default BottomAnimation;
