import React from 'react';
import { AwesomeButton } from "react-awesome-button";
import "react-awesome-button/dist/styles.css";
import {Link} from 'react-router-dom'



class BottomAnimation extends React.Component {
    constructor(props){
        super(props);
        this.state={
            view: this.props.car
        }

    }
    // handleClick = ()=>{
    //     console.log("dziala")
    // }

    componentDidMount =()=>{
        let suprise= 
        <div>
            <AwesomeButton type="secondary" style={{
               border:"10px solid rgb(125, 99, 177)",
               opacity:".6",
               height:"20%",
               fontSize: "5rem",
               position: "absolute",
               transform:"translateY(-50%) translateX(50%)",
               top: "70%",
               right: "50%",
               width:"20%"  
            }}>
                <Link to= "/player" style={{textDecorationLine:"none"}}>Play </Link>
            </AwesomeButton>
        </div>
        this.timeout = setTimeout(()=>{
            this.setState({
             view: suprise
            })
        },4000);

    }
    componentWillUnmount(){
        clearTimeout(this.timeout);
    }

    bootstrap= ()=>{
        <div className="modal fade" id="myModal" role="dialog">
        <div className="modal-dialog">

          <div className="modal-content">
            <div className="modal-header">
              <button type="button" class="close" data-dismiss="modal">&times;</button>
              <h4 className="modal-title">Modal with Dark Overlay</h4>
            </div>
            <div className="modal-body">
              <p>This modal has a dark overlay.</p>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-default" data-dismiss="modal">Close</button>
            </div>
          </div>

        </div>
      </div>

    }
    render(){
        return(
            <div>
                 <button type="button" className="btn btn-info btn-md" id="myBtn">Modal with Overlay (backdrop:true)</button>
            <div> {this.state.view}</div>
            </div>
        )
    }
}



export default BottomAnimation;
