import React from 'react';
import './gameBoard.css'


class Game extends React.Component{



         pokaz=()=> {
            let t; 
          var text = "";
          for (y=0; y<=8; y++) {
            for (x=0; x<=8; x++) {
              text += t[y][x]+" ";
            }
            text += <br/>; //po każdym wierszu, do następnej lini
          }
          //pobranie div id=plansza i ustawienie zawartości

        }





    render(){
        let userRows = prompt("Provide number of rows");
        let userColumns =prompt("Provide number of columns");
    return(
    <div className ="board">{this.create2DArray(userRows,userColumns)}</div>)
    }
}

export default Game;
