import React from 'react';
import FinalGame from './finalGame'




class Hero extends React.Component {

  playerGender = (event) => this.setState({good_bad: event.target.value})


  selected =() =>{
    console.log("dziala")
  }




  render(){
    let imgPeople= <img src={require("./imagies/person.png")} className ="cell" alt="person"/>
    let arrForPeople =[];
    for ( let i=0; i<10; i++){
        arrForPeople.push(imgPeople)
    }
    let intentionResult;
    if (this.props.selectedIntention === "good"){
        intentionResult = ["./imagies/batman.png","./imagies/cat.png"]

           console.log("ccc")
           } else if (this.props.selectedIntention === "bad") {
          intentionResult = ["./imagies/joker-female.png","./imagies/joker.png"]

           }else return null







      return (<div>
          <select value={this.state.good_bad} onChange={this.playerGender} > 
                        <option value ="/">--</option>
                        <option value="good">Good</option>
                        <option value="bad">Bad</option>
                </select> <br></br>
                <div>
                {intentionResult.map((newSrc,index)=>{
                return <img style={{width:"10%", height:"30%"}} key={index} src={require(`${newSrc}`)}
            alt="ccccd" onClick ={this.selected}/> })}</div>
            <FinalGame  position={'absolute'} imgages={arrForPeople}/>
            </div>)



  }}




export default Hero;
