import React from 'react';
import './gameBoard.css'





class BoardForGame extends React.Component{
    constructor(props) {
        super(props);
        this.addActiveClass= this.addActiveClass(this);
        this.state = {
            image: "./imagies/joker-white.jpg",
            counter: 0,
            active: false,




        }}
        toggleClass() {
            const currentState = this.state.active;
            this.setState({ active: !currentState });
        };
        handleClick = (event )=>{
            console.log(event.currentTarget.getAttribute('id'))
            console.log(event)
            // event.currentTarget.classNames({ 'foo-bar': true });



            // if ( event.currentTarget.getAttribute('id')!== event.currentTarget.getAttribute('id')){
            //     console.log("inne")
            // }else {this.setState({
            //     image:"./imagies/batman-black.jpg"
            // })}


        }


        boardGame = (urows, ucolumns) => {
                    // Build the rows in an array
                    let rows = [];
                    let nr = 0 
                    for (let y = 0; y < urows; y++) {
                        nr ++
                        const cells = []; 

                        for (let x = 0; x < ucolumns; x++) {

                            cells.push(<td key={x}><img 
                                        src={require(`${this.state.image}`)}
                                        // onClick={(e) => this.handleClick(e)}
                                        className={this.state.active ? 'your_className': null} 
                                        onclick={this.toggleClass} 
                                        id ={nr++}
                            // className="cell"
                           alt="x" /></td>)

                        }

                        // Put them in the row
                        rows.push(<tr key={y}>{cells}</tr>);

                    }

                  // Return the table
                    return (
                      <table>
                        <tbody>
                          {rows}
                        </tbody>
                      </table>
                    );

                }
                render(){
                    return (
                        <div>{this.boardGame(this.props.userRows,this.props.userColumns)}</div>

                    )
                }
    }

class Game extends React.Component {




  render(){
    let userRows = prompt("Provide number of rows");
    let userColumns = prompt("Provide number of columns");
      return(
          <BoardForGame userRows ={userRows} userColumns={userColumns}/>

      )
  }
    }


export default Game;
