import React from 'react';


class FlagsApi extends React.Component{
    constructor(props){
        super(props);
        this.state = {}
    }

    componentDidMount() {
        console.log(this.state);

        fetch('https://api.ipgeolocation.io/ipgeo?apiKey=080053faad5448749930b6bdb1ec0a7f')

        .then(response => {

            response.json().then(data => {
                this.setState(data);
                console.log(response)
                console.log(data)
            });



        }).catch(err => {
            console.log('Błąd!', err);
        });
    }




    render() {


        return(
            <div>
                <h1>{this.state.country_name}</h1>
                <h2>{this.state.time_zone.current_time}</h2>

                <img src={this.state.country_flag} alt="" />




            </div>
        )
    }
}

export default FlagsApi;
