import React from 'react';

class Flags2 extends React.Component{
    constructor(props){
        super(props);
        this.state = this.props.nameFlag
    }

    componentDidMount() {
        console.log(this.state);
        fetch('https://api.ipgeolocation.io/ipgeo?apiKey=080053faad5448749930b6bdb1ec0a7f')
        .then(response => {
            response.json().then(data => {
                this.setState(data);
            });
        }).catch(err => {
            console.log('Błąd!', err);
        });
    }

    render() {
        return(
            <div>
                <h1>{this.state.country_name}</h1>
                <img src={this.state.country_flag} alt="" />
            </div>
        )
    }
}

export default Flags2;
