import React from 'react';
import FlagsApi from './api'
// import Hero from './heros'
import {Link} from 'react-router-dom'
import './form.css'



class PlayerForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            nick: "",
            email: "",
            good_bad:"",
            country:"",
            finalResult: "",
            color:""
        }
    }
    playerNick = (event) => this.setState({nick: event.target.value})
    playerEmail = (event) => this.setState({email: event.target.value})
    // playerGender = (event) => this.setState({good_bad: event.target.value})
    playerCountry = (event) => this.setState({country: event.target.value})

    handleSubmit = (event) => {
        event.preventDefault(); 
        let result = "";
        if (this.state.nick === "") {
            result = "Entry you nick name"
          this.setState({color:"red"});
        } else if (this.state.email.indexOf("@")<0){
            result = "Entry your email"
          this.setState({color:"red"});
        // } else if
        //  (this.state.good_bad === "") {
        //     result = "Entry your side which you assist "
        //   this.setState({color:"red"});
        }else if ( this.state.country ===""){
            result = "Entry your country"
        } else {

          this.setState({color:"green"});
          result= "Let's start" + this.state.nick
        }

        this.setState({finalResult: result})
    }


    render() {


        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <h1 style={{backgroundColor:this.state.color}}>{this.state.finalResult}</h1>
                    <input type="text" onChange={this.playerNick} value={this.state.nick} placeholder="Your nick" className="newForm"/><br></br>
                    <input type="text" onChange={this.playerEmail} value={this.state.email} placeholder="Your email" className="newForm"/><br></br>
                    {/* <select value={this.state.good_bad} onChange={this.playerGender} > 
                        <option value ="/">--</option>
                        <option value="good">Good</option>
                        <option value="bad">Bad</option>
                </select> <br></br> */}
                <input type="text" onChange={this.playerCountry} value={this.state.country} placeholder="Your name country" className="newForm"/><br></br>
                    <input type="submit" value="Wyślij"className="newForm"/><br></br>

                </form>
                <button className="btnPlay"><Link to= "/player/game" style={{textDecorationLine:"none", color:"rgb(117, 231, 157)"}}>PLAY </Link></button>



                 <FlagsApi/> 


            </div>
        )
    }
}

export default PlayerForm;
