import React from 'react';
import './gameBoard.css'





class BoardForGame extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            image: "./imagies/joker-white.jpg",
            counter: 0

        }}
        handleClick = (e)=>{
            e.preventDefault();
            if ( this.state.image ==="./imagies/joker-white.jpg"){
                this.setState({
                    image: "./imagies/batman-black.jpg",
                    counter: this.state.counter +1


            })
                }else {

                            console.log( "to pole juz bylo klikniete")}
                }


        boardGame = (urows, ucolumns) => {
                    // Build the rows in an array
                    let rows = [];
                    for (let y = 0; y < urows; y++) {
                        const cells = [];
                        for (let x = 0; x < ucolumns; x++) {
                            cells.push(<td key={x}><img
                                        src={require(`${this.state.image}`)}
                                        onClick={(e) => this.handleClick(e)}
                            className="cell"
                           alt="x" /></td>)

                        }

                        // Put them in the row
                        rows.push(<tr key={y}>{cells}</tr>);

                    }

                  // Return the table
                    return (
                      <table>
                        <tbody>
                          {rows}
                        </tbody>
                      </table>
                    );

                }
                render(){
                    return (
                        <div>{this.boardGame(this.props.userRows,this.props.userColumns)}</div>

                    )
                }
    }

class Game extends React.Component {




  render(){
    let userRows = prompt("Provide number of rows");
    let userColumns = prompt("Provide number of columns");
      return(
          <BoardForGame userRows ={userRows} userColumns={userColumns}/>

      )
  }
    }


export default Game;
