import React from 'react';
import ReactDOM from 'react-dom';



class  FinalGame extends React.Component{
    constructor(props){
        super(props);
        this.state={
            list1: ['Sos pomidorowy', 'Ananas', 'Kukurydza', 'Pomidor'],
            list2:[]
        }
    }

    handleSelect = (e, fromlist, toList, elToPush, index) => {
        let dummy1 = this.state[fromlist].slice();
        let dummy2 = this.state[toList].slice();
        dummy1.splice(index, 1);
        dummy2.push(elToPush);

        this.setState({
            [fromlist] : dummy1,
            [toList] : dummy2,
        });
    };



    render(){

        const lista1 = this.state.list1.map((elToPush, index) => {
            return <li key={index} onClick={e => this.handleSelect(e, 'list1', 'list2', elToPush, index)}>
                {elToPush}
            </li>;
        });

        // DODATKOWO: Nie ma w poleceniu. Kiedy chce aby przenioslo z drugiej list spowrotem do pierwszej 
        // const lista2 = this.state.list2.map((elToPush, index) => {
        //     return <li key={index} onClick={e => this.handleSelect(e, 'list2', 'list1', elToPush, index)}>

        //         {elToPush}
        //     </li>;
        // });




        return (
            <div>
                <h1 >
                Lista1:</h1>
                <ul>{list1}</ul>

                <h1>
                    Lista2:</h1>
                <ul>{list2}</ul>


            </div>

        )
    }
}
export default FinalGame;
