import React from 'react';
import './finalGame.css'



class Game extends React.Component{
    return(){
        render(
            <div>pp</div>
        )
    }
}

class FinalGame extends React.Component{
    constructor(props){
        super(props);
        this.state={
            list1: this.props.imgages,
            list2:[],
            counter:0,
            text:"",
            top:"",
            left:"",
            position:this.props.position,
            com:this.props.sth1

        }
    }

    handleSelect = (e, fromlist, toList, elToPush, index) => {
        let dummy1 = this.state[fromlist].slice();
        let dummy2 = this.state[toList].slice();
        dummy1.splice(index, 1);
        dummy2.push(elToPush);

        console.log("list1 " +dummy1)
        console.log("list2 "+ dummy2)

        this.setState({
            [fromlist] : dummy1,
            [toList] : dummy2,
            counter: this.state.counter + 1
        });
    };
    mouseOn =()=>{
        console.log(this.state.com)
        console.log("najechalam")
        let randomNumber = Math.floor((Math.random() * (100-1) + 1));
        var nrPx = (randomNumber) +"px"
        console.log(nrPx)
        this.setState({
           left:nrPx,
            top:nrPx
        })
    }



    componentDidMount =()=>{

        this.timeout = setTimeout(()=>{
            this.setState({
                text: alert(`END GAME YOUR SCORE IS ${this.state.counter} IN 5SECUNDS` )

            })
          },5000);

    }
    componentWillUnmount(){
        clearTimeout(this.timeout);
    }






    render(){ 
        console.log(this.props.choose)



        const lista1 = this.state.list1.map((elToPush, index) => {
            return <li className="people" onMouseEnter={this.mouseOn} 
            style = {{  left:this.state.left,
                top:this.state.top,
                position:this.props.position}} 
                key={index} 
                onClick={e => this.handleSelect(e, 'list1', 'list2', elToPush, index)}>
                {elToPush} 

            </li>;
        });

        const lista2 = this.state.list2.map((elToPush, index) => {
            return <li  className="people" key={index} 
            // onClick={e => this.handleSelect(e, 'list2', 'list1', elToPush, index)}
            >
            {elToPush}
            </li>;
        });




        return (
            <div>
                <div className= "places">
                    <div className="direction">
                    <h1>Dangerous place</h1>
                    <ul >{lista1}</ul>
                    </div>
                    <div className="direction">
                    <h1>Good place </h1>
                    <ul >{lista2}</ul>
                </div>
                </div>


            </div>

        )
    }
}
export default FinalGame;
