import React from 'react';
import './gameBoard.css'


class Game extends React.Component {





    boardGame = (urows, ucolumns) => {
        // Build the rows in an array
        let rows = [];
        for (let y = 0; y < urows; y++) {
            const cells = [];
            for (let x = 0; x < ucolumns; x++) {
                cells.push(<Cell key={x} />);
            }
            // Put them in the row
            rows.push(<tr key={y}>{cells}</tr>);
            console.log(cells)
        }



        // Return the table
        return (
          <table>
            <tbody>
              {rows}
            </tbody>
          </table>
        );
    }
    randomBatmans = (urows1,ucolumns1) =>{
        // count all img 
        let calcBoard = urows1 * ucolumns1
        let randomNr = Math.round(Math.random() * (calcBoard - 1) + 1);
        console.log(randomNr)
        let arrForIndex = []
        for (let i =0; i<randomNr; i++){
            let finalIndex = Math.round(Math.random() * (calcBoard - 1) + 1);
            console.log(finalIndex)
         arrForIndex.push(finalIndex);

        }
        console.log(arrForIndex)





    }

    check = (ar)=>{
        console.log(ar)
    }





    render() {
        let userRows = prompt("Provide number of rows");
        let userColumns = prompt("Provide number of columns");


        return (
            <div>
                <div>{this.boardGame(userRows,userColumns)}</div>
            {/* start generate random index during click on button  */}
                <button onClick={() => this.randomBatmans(userRows,userColumns)}>play</button>

                <button onClick ={() => this.check()}>Check next population</button>

            </div>
        );
    }
}



class Cell extends React.Component {



    render() {
        return (
            <td key={this.props.index}>
            {/* <img
              src={require("./imagies/joker-white.jpg")}
              onClick={this.gameAction}
              className="cell"
              alt="x" /> */}
          </td>
        );
    }
}




export default Game;
