import './gameBoard.css'


class Game extends React.Component{

  boardGame = (urows,ucolumns)=>{
       // Build the rows in an array
       let rows = [];
       let index = 0;
       for (let y = 0; y < urows; y++) {
           // Build the cells in an array
           const cells = [];
           for (let x = 0; x < ucolumns; x++) {
               cells.push(<Cell/>);
               index ++;
           }
           // Put them in the row
           rows.push(<tr>{cells}</tr>);
       }
            // Return the table

       return <table><tbody>{rows}</tbody></table>;

  }
        render() {
            let userRows = prompt("Provide number of rows");
            let userColumns =prompt("Provide number of columns");

            return (<div>{this.boardGame(userRows,userColumns)}</div>);

        }
    }

    class Cell extends React.Component {

        gameAction =() =>{
            console.log("works")
        }
        render() {
            return <td><img src ={require("./imagies/joker-white.jpg")} onClick ={this.gameAction} className="cell" alt = "x"/></td>;
        }
    } 




export default Game;
