import React from 'react';
import './car.css'
import {BrowserRouter as Router, Route} from 'react-router-dom'

import MainAnimation from './mainAnimation';
import BottomAnimation from './bottomAnimation'
import PlayerForm from './player'
// import FlagsApi from './api'
import FinalGame from './finalGame'
import './gameBoard.css'


// const Player = props =>
//     <div>
//         <PlayerForm />
//         <FlagsApi />
//     </div>

class App extends React.Component{
    constructor(props){
        super(props);
        this.state={
            list1: "arrForPeople",
            list2:[],
            counter:0,
            text:"",
            top:"",
            left:"",
            position:this.props.position

        }
    }
    handleSelect = (e, fromlist, toList, elToPush, index) => {
        let dummy1 = this.state[fromlist].slice();
        let dummy2 = this.state[toList].slice();
        dummy1.splice(index, 1);
        dummy2.push(elToPush);

        console.log("list1 " +dummy1)
        console.log("list2 "+ dummy2)

        this.setState({
            [fromlist] : dummy1,
            [toList] : dummy2,
            counter: this.state.counter + 1
        });
    };
    mouseOn =()=>{
        console.log("najechalam")
        let randomNumber = Math.floor((Math.random() * (100-1) + 1));
        var nrPx = (randomNumber) +"px"
        console.log(nrPx)
        this.setState({
           left:nrPx,
            top:nrPx
        })
    }

    render(){
        let batmanCar = <img src={require("./imagies/batman_car.png")} alt="batman car" className ="fastCar"/>
        let imgPeople= <img src={require("./imagies/person.png")} className ="cell" alt="person"/>
        let arrForPeople =[];
            for ( let i=0; i<10; i++){
                arrForPeople.push(imgPeople)
            }

        return(
            <div>
            <Router>
                <Route exact path = "/" component={MainAnimation}/>
                <Route exact path='/' render={() => <BottomAnimation car ={batmanCar}/> }/>
                <Route exact path ='/player' component={PlayerForm} />
                <Route excact path = '/player/game'render={() => <FinalGame imgages={arrForPeople} functosent={this.handleSelect} functosent2={this.mouseOn} position={'absolute'} /> }/>


            </Router>


            </div>
        )
    }
}

export default App;
