import React from 'react';
import './gameBoard.css'



class Game extends React.Component {
    constructor(props){
                super(props)
                    this.state={
                        image:"./imagies/joker-white.jpg",
                        alert:"start",
                        counter: 0


                }
            }
    changeForBatman = () =>{
        //need to change img during click 
        if ( this.state.image ==="./imagies/joker-white.jpg"){
            this.setState({
                image: "./imagies/batman-black.jpg",
                counter: this.state.counter +1


            })
        }else {
            // this.setState({
            //     image: "./imagies/joker-white.jpg"

            // })
            console.log( "to pole juz bylo klikniete")
        }
    }

    boardGame = (urows, ucolumns) => {
        // Build the rows in an array
        let rows = [];
        for (let y = 0; y < urows; y++) {
            const cells = [];
            for (let x = 0; x < ucolumns; x++) {
                cells.push(<td><img
                    src={require(`${this.state.image}`)}

                    onClick = {(e)=>{this.changeForBatman(e.target)}}
                    className="cell"
                alt="x" /></td>);

            }

            // Put them in the row
            rows.push(<tr key={y}>{cells}</tr>);

        }
      // Return the table
        return (
          <table>
            <tbody>
              {rows}
            </tbody>
          </table>
        );
    }




    render() {
        let userRows = prompt("Provide number of rows");
        let userColumns = prompt("Provide number of columns");

        return (
            <div>
                <div>{this.boardGame(userRows,userColumns)}</div>

            {/* start generate random index during click on button  */}
                {/* <button onClick ={() => this.check(musze przekazac cala tablice z funkcji boardGame)}>Check next population</button> */}




            </div>
        );
    }
}



